mapper.select:
select seq, id, content, tag, location, filename, date_created, date_updated, realpathfile from ( select row_number()over(order by seq desc) as rnum, seq, id, content, tag, location, filename, date_created, date_updated, realpathfile from feed where 1=1
and content like concat('%', #{search}, '%')
order by seq desc ) a where rnum between #{start} and #{end}
