mapper.select:
SELECT GRP_FEED_LIKE_ID
FROM GRP_FEED_LIKE
WHERE GRP_FEED_NO = ${grpFeedNo}

mapper.update:
UPDATE GRP
SET GRP_VISIT = GRP_VISIT + 1
WHERE GRP_NO = ${grpNo}

mapper.select:
SELECT *
FROM GRP
WHERE GRP_NO = ${grpNo}

SELECT PROFILE
FROM MEMBER
WHERE ID = #{id}

SELECT GRP_LEADER
FROM GRP
WHERE GRP_NO = ${grpNo}

mapper.update:
UPDATE MEMBER_GROUP
SET STATUS = 1
WHERE GROUP_ID = ${groupId} AND MEMBER_ID = #{memberId}

UPDATE GRP
SET GRP_MEMBER = GRP_MEMBER + 1
WHERE GRP_NO = ${grpNo}

UPDATE GRP
SET GRP_NAME = #{grpName}, GRP_INTRO = #{grpIntro}, GRP_IMAGE = #{grpImage}
WHERE GRP_NO = ${grpNo}

mapper.delete:
DELETE
FROM MEMBER_GROUP
WHERE MEMBER_ID = #{memberId} AND GROUP_ID =#{groupId}

mapper.update:
UPDATE GRP
SET GRP_MEMBER = GRP_MEMBER - 1
WHERE GRP_NO = ${grpNo}

UPDATE GRP_FEED
SET GRP_FEED_CONTENT = #{grpFeedContent}, GRP_FEED_SETTING = #{grpFeedSetting}
WHERE GRP_FEED_NO = ${grpFeedNo}

mapper.select:
SELECT *
FROM GRP_FEED
WHERE GRP_FEED_NO = ${grpFeedNo}

SELECT COUNT(*)
FROM MEMBER_GROUP
WHERE GROUP_ID = ${groupId}

mapper.delete:
DELETE
FROM GRP_FEED_LIKE
WHERE GRP_FEED_NO = ${grpFeedNo} AND GRP_FEED_LIKE_ID = #{grpFeedLikeId}

mapper.select:
SELECT COUNT(*)
FROM GRP_FEED_LIKE
WHERE GRP_FEED_NO = ${grpFeedNo} AND GRP_FEED_LIKE_ID = #{grpFeedLikeId}

SELECT C.*, M.PROFILE
FROM GRP_COMMENT C, MEMBER M
WHERE M.ID = C.GRP_FEED_CMT_ID AND GRP_FEED_NO = ${grpFeedNo}

mapper.insert:
INSERT INTO GRP_COMMENT(GRP_FEED_NO,  GRP_FEED_CMT_ID, GRP_FEED_CMT_REF, GRP_FEED_CMT_CONTENT)
VALUES(${grpFeedNo},
				#{grpFeedCmtId},
				(SELECT IFNULL(MAX(GRP_FEED_CMT_REF), 0) +1 FROM GRP_COMMENT GC),
				#{grpFeedCmtContent}
				)

mapper.update:
UPDATE GRP_COMMENT
SET GRP_FEED_CMT_CONTENT = #{grpFeedCmtContent}, GRP_FEED_CMT_WD = NOW()
WHERE GRP_CMT_NO = ${grpCmtNo}

mapper.select:
SELECT COUNT(*)
FROM GRP_COMMENT
WHERE GRP_FEED_NO = ${grpFeedNo}

SELECT *
FROM GRP_COMMENT
WHERE GRP_CMT_NO = ${grpCmtNo}

SELECT *
FROM CARE_GRP
WHERE CARE_GRP_FEED_NO = ${careGrpFeedNo}
